/* Scroll Reveal
/*--------------------------*/

function initScrollReveal() {
	var skinnyReveal = {
	  delay    : 200,
	  duration    : 1000,
	  origin      : 'bottom',
	  distance : '70px',
	  vFactor: 0.20
	};
	var skinnyRevealLeft = {
	  delay    : 200,
	  duration    : 1000,
	  origin      : 'left',
	  distance : '70px',
	  vFactor: 0.20
	};
	var skinnyRevealRight = {
	  delay    : 200,
	  duration    : 1000,
	  origin      : 'right',
	  distance : '70px',
	  vFactor: 0.20
	};
	var skinnyRevealScalein = {
	  delay    : 200,
	  duration    : 1000,
	  easing   : 'ease-in-out',
	  scale    : .7,
	  vFactor: 0.20
	};
	var skinnyRevealScaleout = {
	  delay    : 200,
	  duration    : 1000,
	  easing   : 'ease-in-out',
	  scale    : 1.2,
	  vFactor: 0.20
	};
	window.sr = ScrollReveal();
    if (sr.isSupported()) {
           document.documentElement.classList.add('sr');
         }
		 if ( jQuery( ".sr .skinny-sr.fade-bottom").length ) {
sr.reveal('.sr .skinny-sr.fade-bottom', skinnyReveal);
}
if ( jQuery( ".sr .skinny-sr.fade-left").length ) {
sr.reveal('.sr .skinny-sr.fade-left', skinnyRevealLeft);
}
if ( jQuery( ".sr .skinny-sr.fade-right").length ) {
sr.reveal('.sr .skinny-sr.fade-right', skinnyRevealRight);
}
if ( jQuery( ".sr .skinny-sr.scale-in").length ) {
sr.reveal('.sr .skinny-sr.scale-in', skinnyRevealScalein);
}
if ( jQuery( ".sr .skinny-sr.scale-out").length ) {
sr.reveal('.sr .skinny-sr.scale-out', skinnyRevealScaleout);
}
if ( jQuery( ".sr .skinny-sr.feature").length ) {
sr.reveal('.sr .skinny-sr.feature', 200);
}
if ( jQuery( ".sr .skinny-sr.image-block").length ) {
sr.reveal('.sr .skinny-sr.image-block', 200);
}
if ( jQuery( ".sr .skinny-sr.another").length ) {
sr.reveal('.sr .skinny-sr.another', 1000);
}
if ( jQuery( ".sr .post .another").length ) {
sr.reveal('.sr .post .another', skinnyRevealScaleout);
}
if ( jQuery( ".sr .post .tag-clouds li").length ) {
sr.reveal('.sr .post .tag-clouds li', 300);
}
}


