   var speed = 0.4;

   function updateBackground($el) {

       var diff = jQuery(window).scrollTop() - $el.offset().top;
       var yPos = -(diff * speed);

       var coords = '50% ' + yPos + 'px';

       $el.css({
           backgroundPosition: coords
       });
   }
   
